    if (this.get("runWhenIdle") && !this._idleIsScheduled && this.get("taskCount") > 0) {
      var self = this;
      setTimeout(
        function(){
          self._idleEntry();
        }, 
        this.get("interval")
      );
      this._idleIsScheduled = YES;
    }
